import React, { useState, useEffect } from "react";
// import ResetForm from "./ResetForm";
import { Input } from "./Input";

const Login = () => {
  return (
    <div className="col-sm-10 offset-1" id="login">
      <h2>Login</h2>
      <form onSubmit={e => e.preventDefault()}><hr />
        <small className="crimson"></small>
        <br />
        <Input
          classNames="form-control login-form"
          value="email"
          type="email"
          action={e => {
              setCredentials({...credentials, email: e.target.value})
          }}
        />
        <Input
          classNames="form-control login-form"
          value="password"
          type="password"
          action={e => {
            setCredentials({...credentials, psw: e.target.value})
        }}
        />

        <div>
          <div className="buttons">
            <button
              type="button" 
              className="btn btn-link violet text-left"  
              onClick={() => expand()}>
              reset
            </button>
            <button
              type="button"
              className="btn btn-link text-right violet"
              style={{ width: "100%", cursor: "pointer" }}
              onClick={() => handleClick()}
            >
              create new account
            </button>
          </div>
          <button
            type="submit"
            className="btn btn-primary bg-violet btn-sm white float-right"
          >
            Submit
          </button>
        </div>
      </form>
      <br />
      <br />
       {/* <ResetForm
       classNames="form-control"
       property="email"
       isExpanded={isExpanded}
    />  */}

    </div>
  );
};

export default Login;
