import React from "react";
import { FavoriteBtn } from './index'

export const Result = ({ movie, toggleFavorite, active }) => {
  return (
    <article className="movie d-flex">
      <div className="p-4 movie d-flex flex-fill">
        {movie.Poster}
        <div className="p-4">
          <h2>
            <a target="_blank" href="#">
              {movie.Title}
            </a>
            <span>({movie.Year})</span>
          </h2>
          <p>{movie.imdbID}</p>
        </div>
      </div>
      <FavoriteBtn active={active} toggleFavorite={toggleFavorite} flex="p-1" />
    </article>
  );
};
