import React from "react";
import { FavoriteBtn } from './index'

export const Result = ({ movie, toggleFavorite, active }) => {
  return (
    <article className="movie d-flex">
      <div className="p-4 movie d-flex flex-fill">
        {movie.poster ? 
          <div
          className="p-1 poster"
          style={{
            backgroundImage: `url(${movie.poster})`,
            backgroundSize: "cover"
          }}
        ></div>
        :
        <div
          className="p-1 poster"
          style={{
            backgroundImage: `url(${process.env.PUBLIC_URL +
              `/placeholder.png`}`,
            backgroundSize: "cover"
          }}
        ></div>
        }
        <div className="p-4">
          <h2>
            <a target="_blank" href="#">
              {movie.title}
            </a>
            <span>({movie.year})</span>
          </h2>
          <p>{movie.imdb}</p>
        </div>
      </div>
      <FavoriteBtn active={active} toggleFavorite={toggleFavorite} flex="p-1" movie={movie}/>
    </article>
  );
};
