import React, { useState, useEffect } from "react";
import {Links, FavoriteBtn, Header, Modal, Result, Results, SearchForm} from './components'

const constants = {
  BASE_URL: 'http://www.omdbapi.com/?',
  API_KEY: 'c044a8f8'
}

const App = () => {
  const [state, setState] = useState({
    isSearching: false,
    active: "search",
    movies: [],
    favorites: [],
    query: "" //resultat de la requête
  });

  const onTextChange = input => {
    setState({ ...state, isSearching: input.length > 0, query: input }) // on verifie si l'user tape ou non
    console.log(input)
  }

  const handleClick = link => {
    setState({...state, active: link})
  }

  const toggleFavorite = movie => {
    const updated = state.movies.map(item => {
      m.imdb === movie
    })
    if (state.active === 'search') {
      setState({...state, favorites: state.favorites.concat(movie)})
    } 
  }

  const fetchAPI = () => {
    if(!state.query) { return }
    const url = `${constants.BASE_URL}s=${state.query}&apikey=${constants.API_KEY}`
    fetch(url)
      .then(res => {
        if(!res.ok) { throw Error(res.statusText)}
        return res.json()
      })
      .then(data => {
        if(data.Response !== 'False')
        setState({...state, movies: create(data.Search)})
      })
  }

  const create = data => {
    return data.map(item => {
      return {title: item.Title, imdb: item.imdbID, year: item.Year, type: item.Type, poster: item.Poster, isFavorite: false}
    })
  }

  useEffect(() => {
    fetchAPI()
  }, [state.query])

  return (
    <div className="App" id="search-container">
      <Header />
      <Links {...state} handleClick={handleClick}/>
      <main>
        <SearchForm onTextChange={onTextChange}/>
        <Results {...state} toggleFavorite={toggleFavorite} />
      </main>
    </div>
  );
};
export default App;
