import React from "react";
// import ResetForm from "./ResetForm";
import { Input } from "./Input";

const Login = () => {

  const handleClick = () => {
    const signupEl = document.getElementById('signup')
    signupEl.scrollIntoView({
      behavior: "smooth"
    })
  }

  return (
    <div className="col-sm-10 offset-1" id="login">
      <h2>Login</h2>
      <form onSubmit={e => e.preventDefault()}><hr />
        <small className="crimson"></small>
        <br />
        <Input
          classNames="form-control login-form"
          value="email"
          type="email"
        />
        <Input
          classNames="form-control login-form"
          value="password"
          type="password"
        />

        <div>
          <div className="buttons">
            <button
              type="button"
              className="btn btn-link violet text-left">
              reset password
            </button>
            <button
              type="button"
              className="btn btn-link text-right violet"
              style={{ width: "100%", cursor: "pointer" }}
              onClick={handleClick}>
              create new account
            </button>
          </div>
          <button
            type="submit"
            className="btn btn-primary bg-violet btn-sm white float-right"
          >
            Submit
          </button>
        </div>
      </form>
      <br />
      <br />
      {/* <ResetForm
       classNames="form-control"
       property="email"
       isExpanded={isExpanded}
    />  */}

    </div>
  );
};

export default Login;
