import React from "react";

export const Links = ({currentUser, active, handleClick}) => (
  <nav>
    {["search", "favorites"].map(link => {
      const isDisabled = !currentUser && link === "favorites" ? "disabled ": "hover"
      eslint-disable-next-line
      return  <a 
                onClick={() => !currentUser ? null : handleClick(link)} 
                className={`${isDisabled} ${active === link && 'active'} `}
              >
              {link.toUpperCase()}
            </a>;
    })}
  </nav>
);
