import React from "react";
import { Result } from './index'

export const Results = ({ movies, favorites, isSearching, active, toggleFavorite }) => {
  const results = active === 'search' ? movies : favorite

  if (active === "favorites"  && favorites.length <= 0) {
    return <p>No Favorites in the list :(</p>;
  }
  if (active === "favorites" && favorites.length > 0) {
    return favorites.map(favorite => {
      return <Result toggleFavorite={toggleFavorite} active={active}/>;
    });
  }
  if (!isSearching) {
    return <p>No results :(</p>;
  }
  return movies.map(movie => {
    return <Result toggleFavorite={toggleFavorite} movie={movie}/>;
  });
};
