import React, { useState } from "react";

export const FavoriteBtn = ({ toggleFavorite, active }) => {

  const [isFavorite, setIsFavorite] = useState(false)

  return (
    <button 
      className={isFavorite ? 'favorite' : 'not-favorite'}
      onClick={() => {
        if (active === 'favorite') {
          toggleFavorite('remove')
        } else {
          toggleFavorite('remove')
        }
        setIsFavorite(!isFavorite)

        }}>

      {
        isFavorite ? 
          <><i className="fas fa-star"></i> <span>&nbsp;Remove</span></>
        :
          <><i className="far fa-star"></i> <span>&nbsp;Add Favorites</span></>
      }
    </button>
  );
};
