import React from "react";

export const Links = ({active, handleClick}) => (
  <nav>
    {["search", "favorites"].map(link => {
      return <a 
              onClick={() => handleClick(link)} 
              className='hover'>
              {
                link.toUpperCase()}
            </a>;
    })}
  </nav>
);
