import React, { useState } from "react";
import {Links, FavoriteBtn, Header, Modal, Result, Results, SearchForm} from './components'

constants = {
  BASE_URL: 'http://www.omdbapi.com/?',
  API_KEY: 'c044a8f8'
}

const App = () => {
  const [state, setState] = useState({
    isSearching: false,
    active: "search",
    movies: [{}, {}],
    favorites: [],
    query: "" //resultat de la requête
  });

  const onTextChange = input => {
    setState({ ...state, isSearching: input.length > 0, query: input }) // on verifie si l'user tape ou non
    console.log(input)
  }

  const handleClick = link => {
    setState({...state, active: link})
  }

  const toggleFavorite = () => {
    if (state.active === 'search') {
      setState({...state, favorites: state.favorites.concat({})})
    } else {
      setState({...state, favorites: [state.favorites.pop()]})
    }
  }

  const fetchAPI = () => {
    const url = `${constants.BASE_URL}`
  }

  return (
    <div className="App" id="search-container">
      <Header />
      <Links {...state} handleClick={handleClick}/>
      <main>
        <SearchForm onTextChange={onTextChange}/>
        <Results {...state} toggleFavorite={toggleFavorite} />
      </main>
    </div>
  );
};
export default App;
